import React, { useEffect, useState } from 'react';
import logo from './logo.svg';
import styles from './TableItem.module.scss';
import { getValue } from '@testing-library/user-event/dist/utils';

function TableItem({
  user,
  submitResult,
  problemList,
  tpl,
  data,
}: {
  user: User;
  submitResult: string[];
  problemList: Problem[];
  tpl: TotalProblem[];
  data: Data[];
}) {
  // const resType = ['ac', 'tle', 'wa', 'pac', 'pac', 'rte'];
  const resType = 'ac';
  const [stack, setStack] = useState(0);
  const [stackInd, setStackInd] = useState(0);

  const getTax = (val: number) => {
    if (val <= 2) return val * 1000;
    else if (val <= 4) return val * 3000;
    else if (val <= 6) return val * 6000;
    else return val * 10000;
  };

  useEffect(() => {
    if (tpl.length > 0 && data.length > 0) {
      const filtered = tpl.map((ele: TotalProblem, ind: number) => {
        const neccessary = ele.problems.filter(
          (ele, index) =>
            !ele.isChallenge &&
            data[ind].data.data.filter((e) => e.user.id === user.id)[0]
              .submitResult[index] !== 'ac',
        ).length;
        const challenge = ele.problems.filter(
          (ele, index) =>
            ele.isChallenge &&
            data[ind].data.data.filter((e) => e.user.id === user.id)[0]
              .submitResult[index] === 'ac',
        ).length;
        if (ind === 0) return { neccessary: neccessary, challenge: 0 };
        return { neccessary: neccessary, challenge: challenge };
      });
    }
  }, [tpl, data]);

  useEffect(() => {
    if (stackInd === 0 && stack === 0) {
    }
  }, [stack]);

  return (
    <tr className={styles['alert']} role="alert">
      <td className={styles['handle-' + user.tier]}>{user.id}</td>
      {submitResult.map((ele) => {
        return (
          <td className={styles['status']}>
            <span className={styles[ele]}>{ele.toUpperCase()}</span>
          </td>
        );
      })}
      <td className={styles['table-space-left']}>&nbsp;</td>
      <td className={styles['table-space']}>&nbsp;</td>
      <td className={styles[finalTax > 0 ? 'table-tax-d' : 'table-tax']}>
        {(finalTax > 0 ? finalTax : 0) + '원'}
      </td>
    </tr>
  );
}

export default TableItem;
